import React, { useState } from "react";
import imagenes from "../assets/imagenes"; // Asegúrate que este archivo tenga tus imágenes
import "../prueba.css";

const GaleriaSimple = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [modalImage, setModalImage] = useState("");

  // Nombres de las imágenes que correspondan a imagenes["img1"], imagenes["img2"], etc.
  const imageKeys = [
    "img11", "img12",
    "img21", "img22",
    "img31", "img32",
    "img51", "img52",
    "img53", "img54",
  ];

  const abrirModal = (src) => {
    setModalImage(src);
    setModalOpen(true);
  };

  const cerrarModal = () => setModalOpen(false);

  return (
    <>
      <div className="galeria-container">
        {imageKeys.map((key, index) => (
          <div key={index} className="galeria-item">
            <img
              src={imagenes[key]}
              alt={`Imagen ${index + 1}`}
              onClick={() => abrirModal(imagenes[key])}
              className="galeria-thumb"
            />
          </div>
        ))}
      </div>

      {modalOpen && (
        <div className="modal-overlay" onClick={cerrarModal}>
          <div className="modal">
            <span className="close-btn" onClick={cerrarModal}>
              &times;
            </span>
            <img src={modalImage} alt="Vista ampliada" className="modal-img" />
          </div>
        </div>
      )}
    </>
  );
};

export default GaleriaSimple;
