import React, { useState } from "react";
import imagenes from "./assets/imagenes"; // ✅ Importamos las imágenes desde el archivo

const Galeria = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [modalImage, setModalImage] = useState("");

  const album = [
    ["img11", "img12"],
    ["img21", "img22"],
    ["img31", "img32"],
    ["img51", "img52"],
    ["img53", "img54"]
  ];

  const abrirModal = (imagen) => {
    setModalImage(imagen);
    setModalOpen(true);
  };

  const cerrarModal = () => {
    setModalOpen(false);
  };

  return (
    <>
      {/* Galería de imágenes */}
      <div id="grid" className="galeria">
        <div className="container-grid">
          {album.map((columna, index) => (
            <div key={index} className="column">
              {columna.map((nombreImg) => (
                <img
                  key={nombreImg}
                  className="object-grid show-p-y active-p-y"
                  src={imagenes[nombreImg]}
                  alt={`Foto ${nombreImg}`}
                  onClick={() => abrirModal(imagenes[nombreImg])} // Abre el modal al hacer clic
                />
              ))}
            </div>
          ))}
          <div className="column full-width show-p-y active-p-y">
            <img
              className="object-grid"
              src={imagenes.img41}
              alt="Foto especial"
              onClick={() => abrirModal(imagenes.img41)}
            />
          </div>
        </div>
      </div>

      {/* Modal de imagen */}
      {modalOpen && (
        <div className="modal" onClick={cerrarModal}>
          <span className="close-btn">&times;</span>
          <img className="modal-img" src={modalImage} alt="Imagen ampliada" />
        </div>
      )}
    </>
  );
};

export default Galeria;
