import React, { useState } from "react";
import "./invit.css";
import FotoSection from "./FotoSection";
import imagenes from "./assets/imagenes";

const Invitacion = () => {
  const [nombre, setNombre] = useState("Invitado");
  const [invitados, setInvitados] = useState(1);
  const [linkGenerado, setLinkGenerado] = useState("");

  // Función para actualizar datos y guardar en Firebase
  const actualizarDatos = async (nuevoNombre, nuevaCantidad) => {
    // Limpieza del nombre
    let nombreLimpio = nuevoNombre.replace(/\s+/g, " ").trim();
  
    // Si el nombre tiene más de 11 caracteres, separarlo en una nueva línea
    let nombreFinal = nombreLimpio.length > 11 
      ? nombreLimpio + ",\n" // ✅ Salto de línea SOLO para el nombre
      : nombreLimpio;
    let textoInvitacion = "estás invitado a mi Fiesta"; // ✅ Mantiene el texto en una línea independiente
  
    setNombre(nombreFinal);
    setInvitados(Math.min(parseInt(nuevaCantidad, 10), 25));
  
    const idInvitacion = Date.now();
    const nuevoLink = `https://tuweb.com/invitaciones/${idInvitacion}`;
    setLinkGenerado(nuevoLink);
  
    try {
      await addDoc(collection(db, "invitaciones"), {
        id: idInvitacion,
        nombre: nombreFinal, // ✅ Nombre en línea separada
        textoInvitacion: textoInvitacion, // ✅ Texto de invitación en otra línea
        invitados: nuevaCantidad,
        link: nuevoLink,
        timestamp: new Date(),
      });
    } catch (error) {
      console.error("Error al guardar la invitación:", error);
    }
  }; 

  return (
    <div>
      <FotoSection imageSrc={imagenes.img1} />
      <FotoSection imageSrc={imagenes.img2} />
      <FotoSection imageSrc={imagenes.img3} /> 
      <FotoSection imageSrc={imagenes.img4} buttonText1="Ubicacion" buttonLink1="https://www.youtube.com/"/>
      <FotoSection imageSrc={imagenes.img5} buttonText1="Ubicacion" buttonLink1="https://maps.google.com/ubicacion2" />
      <FotoSection imageSrc={imagenes.img6}/>
      <FotoSection imageSrc={imagenes.img7}/>
      <FotoSection imageSrc={imagenes.img8}/>
      <FotoSection
        imageSrc={imagenes.img9}
        buttonText1="Confirmar asistencia"
        buttonLink1="https://api.whatsapp.com/send?phone=+525619060019&text=Gracias por invitarme!"
        buttonText2="No puedo asistir"
        buttonLink2="https://api.whatsapp.com/send?phone=+525619060019&text=No podre asistir."
        invitacion={{
          nombre: `${nombre} estás invitado a mi Fiesta`,
          invitados: `Invitación para ${invitados} adulto(s)`,
        }}
      />
    </div>
  );
};

export default Invitacion;
