const ConfirmacionAsistencia = ({ db }) => {
  const [nombre, setNombre] = useState("");

  const manejarConfirmacion = async (asistencia) => {
    if (!nombre.trim()) {
      console.error("❌ Error: `nombre` no tiene un valor válido.");
      return;
    }

    const idInvitacion = nombre.replace(/\s+/g, "").toLowerCase(); // Normaliza el ID
    const estadoAsistencia = asistencia ? "Asistirá" : "No asistirá";
    const mensajeWhatsApp = asistencia
      ? `Confirmo asistencia. Mi nombre es ${nombre}.`
      : `No puedo asistir. Mi nombre es ${nombre}.`;
    const whatsappLink = `https://wa.me/521XXXXXXXXXX?text=${encodeURIComponent(mensajeWhatsApp)}`;

    try {
      // Guardar en Firebase (cambia `db` por tu instancia de Firestore)
      await setDoc(doc(db, "eventos", "invitaciones", idInvitacion), {
        id: idInvitacion,
        nombre,
        asistencia: estadoAsistencia,
        timestamp: new Date(),
      });

      console.log(`✅ Asistencia guardada: ${nombre} - ${estadoAsistencia}`);

      // Redirigir a WhatsApp
      window.open(whatsappLink, "_blank");
    } catch (error) {
      console.error("❌ Error al guardar la asistencia:", error);
    }
  };
}

