import React, { useState } from "react";
import "./FormGen.css"

const FormGen = ({ actualizarDatos }) => {
  if (!actualizarDatos) {
    console.error("Error: La función `actualizarDatos` no fue pasada a `Invitacion`.");
  }

  const [nombre, setNombre] = useState("");
  const [invitados, setInvitados] = useState(1);

  const manejarCambioNombre = (e) => {
    let valor = e.target.value.replace(/\s+/g, " ").trim(); // Limpieza de espacios extra
  
    // Permitir solo letras y espacios
    if (!/^[a-zA-ZáéíóúÁÉÍÓÚñÑ\s]*$/.test(valor)) return;
  
    // Mostrar alerta si supera los 22 caracteres
    if (valor.length > 22) {
      alert("El nombre no puede tener más de 22 caracteres.");
      return;
    }
  
    setNombre(valor);
  };
  
  const manejarCambioInvitados = (e) => {
    let valor = parseInt(e.target.value, 10);
  
    // Permitir solo números y limitar a 25
    if (isNaN(valor) || valor < 1 || valor > 25) {
      alert("El número de invitados debe estar entre 1 y 25.");
      return;
    }
  
    setInvitados(valor);
  };
  
  return (
    <form onSubmit={manejarEnvio}>
      <label>Nombre de la Familia:</label>
      <input 
        type="text" 
        value={nombre} 
        onChange={manejarCambioNombre} 
        required 
      />
  
      <label>Número de Invitados:</label>
      <input 
        type="number" 
        value={invitados} 
        min="1" 
        max="25" 
        onChange={manejarCambioInvitados} 
        required 
      />
  
      <button type="submit">Generar Invitación</button>
    </form>
  );
}      

export default FormGen;
