import React, { useState, useEffect } from "react";
import axios from "axios";
import { db } from "./Database/firebaseConfig";
import { collection, addDoc } from "firebase/firestore";
import FotoSection from "./FotoSection";
import FormGen from "./componentes/FormGen";
import imagenes from "./assets/imagenes";
import "./invit.css";

const App = () => {
  const [nombre, setNombre] = useState("Invitado");
  const [invitAdultos, setInvitadosAdultos] = useState(1);
  const [invitNiÃ±os, setInvitadosNiÃ±os] = useState(0);
  const [includNiÃ±os, setIncluirNiÃ±os] = useState(false);
  const [totalInvitados, setTotalInvitados] = useState(1);
  const [linkGenerado, setLinkGenerado] = useState("");
  const [linkAcortado, setLinkAcortado] = useState("");

  // ðŸ“Œ FunciÃ³n para actualizar los datos y guardar en Firebase
  const actualizarDatos = async (nuevoNombre, nuevaCantidadAdultos, nuevaCantidadNiÃ±os, incluirNiÃ±os) => {
    let nombreLimpio = nuevoNombre.replace(/\s+/g, " ").trim();
    let nombreFinal = nombreLimpio.length > 11 ? nombreLimpio + ",\n" : nombreLimpio;
    const idInvitacion = Date.now();
    const nuevoLink = `https://tuweb.com/invitaciones/${idInvitacion}`;

    // âœ… Calcular `totalInvitados` antes de actualizar el estado
    const totalInvitadosCalculado = nuevaCantidadAdultos + (includNiÃ±os ? nuevaCantidadNiÃ±os : 0);

    setNombre(nombreFinal);
    setInvitadosAdultos(nuevaCantidadAdultos);
    setInvitadosNiÃ±os(includNiÃ±os ? nuevaCantidadNiÃ±os : 0);
    setTotalInvitados(totalInvitadosCalculado); // âœ… Se asigna sin esperar a `useEffect`
    setLinkGenerado(nuevoLink);

    try {
      await addDoc(collection(db, "invitaciones"), {
        id: idInvitacion,
        nombre: nombreFinal,
        invitAdultos: nuevaCantidadAdultos,
        invitNiÃ±os: includNiÃ±os ? nuevaCantidadNiÃ±os : 0,
        totalInvitados: totalInvitadosCalculado, // âœ… Se almacena con valor inmediato
        link: nuevoLink,
        timestamp: new Date(),
      });

      const response = await axios.get(`https://tinyurl.com/api-create.php?url=${nuevoLink}`);
      setLinkAcortado(response.data);
    } catch (error) {
      console.error("Error al guardar la invitaciÃ³n:", error);
    }
  };


  // ðŸ“Œ FunciÃ³n para copiar el enlace
  const copiarAlPortapapeles = () => {
    navigator.clipboard.writeText(linkAcortado || linkGenerado);
    alert("Enlace copiado al portapapeles.");
  };

  return (
    <div>
      <FormGen actualizarDatos={actualizarDatos} />

      {linkGenerado && (
        <div>
          <p>
            Tu invitaciÃ³n ha sido generada:{" "}
            <a href={linkAcortado || linkGenerado} target="_blank" rel="noopener noreferrer">
              {linkAcortado || linkGenerado}
            </a>
          </p>
          <button onClick={copiarAlPortapapeles}>ðŸ“‹ Copiar enlace</button>
        </div>
      )}

      <FotoSection
        imageSrc={imagenes.img9}
        buttonText1="Confirmar asistencia"
        buttonLink1="https://api.whatsapp.com/send?phone=+525619060019&text=Gracias por invitarme!"
        buttonText2="No puedo asistir"
        buttonLink2="https://api.whatsapp.com/send?phone=+525619060019&text=No podre asistir."
        invitacion={{
          nombre: `${nombre}, estÃ¡s invitado a mi Fiesta`,
          invitados: `InvitaciÃ³n para ${totalInvitados} personas`, // âœ… Ahora toma el valor correcto
        }}
      />
    </div>
  );
};

export default App;
