import React, { useState } from "react";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { setDoc, doc, getDoc } from "firebase/firestore";
import { auth, db } from "./firebaseConfig";

const RegistroCliente = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nombreCliente, setNombreCliente] = useState("");
  const [categoria, setCategoria] = useState("XV A√±os"); // Puedes hacerlo din√°mico

  // ‚úÖ Registro en /Clientes/Categorias/{categoria}/{nombreCliente}
  const registrarCliente = async (email, categoria, nombreCliente) => {
    const ref = doc(db, "Clientes", "Categorias", categoria, nombreCliente);
    const existe = await getDoc(ref);

    if (existe.exists()) {
        throw new Error(`‚ö†Ô∏è Ya existe un cliente llamado "${nombreCliente}" en la categor√≠a "${categoria}". Usa otro nombre o edita el cliente existente.`);
    }

    await setDoc(ref, {
        nombreCliente,
        categoria,
        correo: email,
        rutaPersonalizada: `/${categoria}/${nombreCliente}/invitaciones`
    });

    console.log(`‚úÖ Cliente guardado en /Clientes/Categorias/${categoria}/${nombreCliente}`);
    };

  const handleRegister = async () => {
    if (!email || !password || !nombreCliente || !categoria) {
      alert("‚ö†Ô∏è Por favor, completa todos los campos.");
      return;
    }

    try {
    const userCred = await createUserWithEmailAndPassword(auth, email, password);
    await registrarCliente(email, categoria, nombreCliente);
    alert("‚úÖ Registro exitoso");
    onLogin(userCred.user);
    } catch (err) {
        alert(err.message); // ya muestra si el nombre est√° duplicado
    }
  };

  return (
    <div>
      <h2>üìù Registro de Cliente</h2>
      <input
        value={nombreCliente}
        onChange={(e) => setNombreCliente(e.target.value)}
        placeholder="Nombre del cliente (ej. Briseyda)"
      />
      <input
        value={categoria}
        onChange={(e) => setCategoria(e.target.value)}
        placeholder="Categor√≠a del evento (ej. XV A√±os)"
      />
      <input
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Correo"
      />
      <input
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Contrase√±a"
        type="password"
      />
      <button onClick={handleRegister}>Registrarse</button>
    </div>
  );
};

export default RegistroCliente;
