import React, { useState } from "react";
import "./FormGen.css"

const FormGen = ({ actualizarDatos }) => {
  if (!actualizarDatos) {
    console.error("Error: La función `actualizarDatos` no fue pasada a `Invitacion`.");
  }

  const [nombre, setNombre] = useState("");
  const [invitados, setInvitados] = useState(1);

  const manejarEnvio = (e) => {
    e.preventDefault();
    actualizarDatos(nombre, invitados); // ✅ Envía los datos a `App.js`
  };

  return (
    <section id="asistencia">
      <div className="card">
        <h1>Genera tus Invitaciones</h1>
        <h2>Ingresa el numero de boletos por invitacion asi como el nombre de la Familia y/o invitado</h2>
        <form onSubmit={manejarEnvio}>
          <label>Nombre de la Familia:</label>
          <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required />

          <label>Número de Invitados:</label>
          <input type="number" value={invitados} min="1" onChange={(e) => setInvitados(e.target.value)} required />

          <button type="submit">Generar Invitación</button>
        </form>
      </div>
    </section>
  );
};

export default FormGen;
