import React, { useState, useEffect } from "react";
import { collection, getDocs } from "firebase/firestore";
import { db } from "../Database/firebaseConfig";

const MostrarInvitaciones = () => {
  const [invitaciones, setInvitaciones] = useState([]);

  useEffect(() => {
    const obtenerInvitaciones = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "invitaciones"));
        const data = querySnapshot.docs.map(doc => doc.data());
        setInvitaciones(data);
      } catch (error) {
        console.error("❌ Error al obtener las invitaciones:", error);
      }
    };

    obtenerInvitaciones();
  }, []);

  return (
    <div>
      <h2>Lista de Invitaciones Guardadas</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Adultos</th>
            <th>Niños</th>
            <th>Enlace</th>
          </tr>
        </thead>
        <tbody>
          {invitaciones.length > 0 ? (
            invitaciones.map((invitacion, index) => (
              <tr key={index}>
                <td>{invitacion.nombre}</td>
                <td>{invitacion.adultos}</td>
                <td>{invitacion.niños}</td>
                <td><a href={invitacion.link} target="_blank" rel="noopener noreferrer">Ver Invitación</a></td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No hay invitaciones registradas aún.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default MostrarInvitaciones;
