import React, { useState, useEffect } from "react";
import { useLocation, useParams } from "react-router-dom";
import { auth } from "./firebaseConfig";
import { onAuthStateChanged } from "firebase/auth";
import Login from "./Login";
import App from "../App";

const Showlog = () => {
  const [usuario, setUsuario] = useState(null);
  const [clienteURL, setClienteURL] = useState("");
  const [cargando, setCargando] = useState(true);

  const location = useLocation();
  const query = new URLSearchParams(location.search);
  const { cliente } = useParams(); // si tu ruta es /login/:cliente

  useEffect(() => {
    const clienteParam = cliente || query.get("cliente") || "Invitado";
    setClienteURL(clienteParam);
  }, [cliente, query]);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setUsuario(user);
      setCargando(false);
    });
    return () => unsubscribe();
  }, []);

  if (cargando) return <div>âŒ› Cargando...</div>;

  return usuario ? (
    <App usuario={usuario} nombreCliente={clienteURL} />
  ) : (
    <Login
      onLogin={(user) => {
        setUsuario(user);
      }}
    />
  );
};

export default Showlog;
