import React, { useState } from "react";
import { db } from "./firebaseConfig";
import { collection, doc, getDocs, setDoc } from "firebase/firestore";
import { signInWithEmailAndPassword, signOut, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "./firebaseConfig";
import "./Login.css"

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mostrarPassword, setMostrarPassword] = useState(false);

  // ‚úÖ Buscar ruta personalizada en Firestore
  const obtenerRutaPersonalizada = async (email) => {
    const categorias = ["XV A√±os", "Eventos Empresariales", "Bodas"]; // o hazlo din√°mico

    for (const categoria of categorias) {
        const clientesRef = collection(db, "Clientes", "Categorias", categoria);
        const snapshot = await getDocs(clientesRef);

        for (const docSnap of snapshot.docs) {
        const data = docSnap.data();
        if (data.correo === email) {
            return data.rutaPersonalizada;
        }
        }
    }

    throw new Error("Cliente no registrado en Firestore.");
    };

  const handleLogin = async () => {
    try {
      const userCred = await signInWithEmailAndPassword(auth, email, password);
      const userEmail = userCred.user.email;
      const ruta = await obtenerRutaPersonalizada(userEmail);

      onLogin(userCred.user);
      window.location.href = ruta;
    } catch (err) {
      alert("‚ùå Error al iniciar sesi√≥n o encontrar la ruta: " + err.message);
    }
  };

  const handleLogout = () => {
    signOut(auth).then(() => {
      alert("üëã Sesi√≥n cerrada");
      onLogin(null);
    });
  };

  return (
    <div className="fondo-login">
    <div className="login-container">
        <h2 style={{ textAlign: "center", marginBottom: "30px" }}>Iniciar sesi√≥n</h2>

        <input
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Correo"
        />

        <input
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Contrase√±a"
        type={mostrarPassword ? "text" : "password"}
        />

        <button onClick={() => setMostrarPassword((prev) => !prev)}>
        {mostrarPassword ? "üôà Ocultar contrase√±a" : "üëÅÔ∏è Ver contrase√±a"}
        </button>

        <button onClick={handleLogin}>Iniciar sesi√≥n</button>
  </div>
  </div>
  );
};

export default Login;
