import React, { useState, useEffect } from "react";
import { doc, getDoc } from "firebase/firestore";
import { db } from "../Database/firebaseConfig";
import "./FormGen.css";

const FormGen = ({ actualizarDatos, handleCompartir, linkGenerado, nombreCliente, idInvitacion }) => {  
  const [nombre, setNombre] = useState("");
  const [invitadosAdultos, setInvitadosAdultos] = useState(1);
  const [invitadosNi√±os, setInvitadosNi√±os] = useState(0);
  const [incluirNi√±os, setIncluirNi√±os] = useState(false);

  useEffect(() => {
    const obtenerDatosInvitado = async () => {
      if (!idInvitacion) return;

      try {
        const docRef = doc(db, `clientes/${nombreCliente}/invitaciones`, idInvitacion);
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
          const data = docSnap.data();
          setNombre(data.nombre);
          setInvitadosAdultos(data.invitadosAdultos);
          setInvitadosNi√±os(data.invitadosNi√±os);
          setIncluirNi√±os(data.incluirNi√±os);
        }
      } catch (error) {
        console.error("‚ùå Error al obtener los datos del invitado:", error);
      }
    };

    obtenerDatosInvitado();
  }, [idInvitacion]);

  const manejarEnvio = (e) => {
    e.preventDefault();
    actualizarDatos(nombreCliente, nombre, invitadosAdultos, invitadosNi√±os, incluirNi√±os);
  };

  return (
    <section id="asistencia">
      <div className="card">
        <h1>Genera tu Invitaci√≥n</h1>
        <form onSubmit={manejarEnvio}>
          <label>Nombre del Invitado:</label>
          <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required />

          <label>N√∫mero de Invitados (Adultos):</label>
          <input type="number" value={invitadosAdultos} min="1" max="25" onChange={(e) => setInvitadosAdultos(parseInt(e.target.value, 10))} required />

          <label>
            <input type="checkbox" checked={incluirNi√±os} onChange={() => setIncluirNi√±os(!incluirNi√±os)} /> ¬øIncluir ni√±os?
          </label>

          {incluirNi√±os && (
            <>
              <label>N√∫mero de Invitados (Ni√±os):</label>
              <input type="number" value={invitadosNi√±os} min="0" max="25" onChange={(e) => setInvitadosNi√±os(parseInt(e.target.value, 10))} required />
            </>
          )}

          <button type="submit">Generar Invitaci√≥n</button>

          {linkGenerado && (
            <button type="button" onClick={handleCompartir}>üì§ Compartir Invitaci√≥n</button>
          )}
        </form>
      </div>
    </section>
  );
};

export default FormGen;
