import React, { useState } from "react";
import "./FormGen.css";

const FormGen = ({ actualizarDatos, handleCompartir, linkGenerado }) => {
  const [nombre, setNombre] = useState("");
  const [invitadosAdultos, setInvitadosAdultos] = useState(1);
  const [invitadosNi침os, setInvitadosNi침os] = useState(0);
  const [incluirNi침os, setIncluirNi침os] = useState(false);

  const manejarCambioNombre = (e) => {
    let valor = e.target.value.replace(/\s{2,}/g, " ");
    if (!/^[a-zA-Z치칠칤칩칰츼칄칈칍칔침칌\s.,\-\/]*$/.test(valor)) {
      alert("Solo puedes introducir texto y los caracteres especiales: . , - /");
      return;
    }
    if (valor.length > 22) {
      alert("El nombre no puede tener m치s de 22 caracteres.");
      return;
    }
    setNombre(valor);
  };

  const manejarCambioInvitadosAdultos = (e) => {
    let numeroInvitados = parseInt(e.target.value, 10);
    if (!numeroInvitados || numeroInvitados < 1 || numeroInvitados > 25) {
      alert("El n칰mero de invitados adultos debe estar entre 1 y 25.");
      return;
    }
    setInvitadosAdultos(numeroInvitados);
  };

  const manejarCambioInvitadosNi침os = (e) => {
    let numeroInvitados = parseInt(e.target.value, 10);
    if (!numeroInvitados || numeroInvitados < 0 || numeroInvitados > 25) {
      alert("El n칰mero de invitados ni침os debe estar entre 0 y 25.");
      return;
    }
    setInvitadosNi침os(numeroInvitados);
  };

  const manejarCheckbox = () => {
    setIncluirNi침os(!incluirNi침os);
    if (!incluirNi침os) {
      setInvitadosNi침os(prev => prev > 0 ? prev : 1);
    }
  };

  const manejarEnvio = (e) => {
    e.preventDefault();
    const totalInvitados = invitadosAdultos + (incluirNi침os ? invitadosNi침os : 0);
    actualizarDatos(nombre, invitadosAdultos, incluirNi침os ? invitadosNi침os : 0, incluirNi침os, totalInvitados);
  };

  return (
    <section id="asistencia">
      <div className="card">
        <h1>Genera tu Invitaci칩n</h1>
        <form onSubmit={manejarEnvio}>
          <input type="text" value={nombre} onChange={manejarCambioNombre} required />
          <input type="number" value={invitadosAdultos} min="1" max="25" onChange={manejarCambioInvitadosAdultos} required />
          {incluirNi침os && (
            <input type="number" value={invitadosNi침os} min="0" max="25" onChange={manejarCambioInvitadosNi침os} required />
          )}
          <button type="submit">Generar Invitaci칩n</button>
          {linkGenerado && <button type="button" onClick={handleCompartir}>游닋 Compartir Invitaci칩n</button>}
        </form>
      </div>
    </section>
  );
};

export default FormGen;
