import React, { useState, useEffect, useRef } from "react";
import imagenes from "./assets/imagenes"; // Importación correcta
import "./Invit3.css"; // Archivo CSS para estilos

const Invitacion3 = () => {
    const [sobreAbierto, setSobreAbierto] = useState(false);
    const sobreRef = useRef(null);
    const musicRef = useRef(null);
    const sobreParamsRef = useRef(null);

    useEffect(() => {
        if (sobreAbierto) {
            musicRef.current.play();
            loadAnimations();
        }
    }, [sobreAbierto]);

    const loadAnimations = () => {
        const translateY = sobreParamsRef.current?.getAttribute("translateY") || "100%";
        const duration = sobreParamsRef.current?.getAttribute("duration") || "3";

        document.querySelector(".date").style.transform = "translateY(6vw) scale(0)";
        document.querySelector(".event").style.transform = "translateY(6vw) scale(0)";
        document.querySelector(".person").style.transform = "scale(0)";
        document.querySelector(".age").style.transform = "translateY(14vw)";
        document.querySelector(".mensaje").style.transform = "scale(0)";
        document.querySelector(".tarjet").style.transform = "translateX(-90vw)";
        document.querySelector(".decorations").style.opacity = "0";

        setTimeout(() => {
            document.querySelector(".date").style.transform = "translateY(0) scale(1)";
        }, 1000);

        setTimeout(() => {
            document.querySelector(".sello").style.opacity = "0";
        }, 500);

        setTimeout(() => {
            document.querySelector(".first").style.transform = "translateY(-100%)";
            document.querySelector(".second").style.transform = `translateY(${translateY})`;
            setTimeout(() => {
                sobreRef.current.remove();
            }, duration * 1000);
        }, 3000);

        setTimeout(() => {
            document.querySelector(".age").style.transform = "translateY(0)";
        }, 2800);

        setTimeout(() => {
            document.querySelector(".decorations").style.opacity = "1";
        }, 4300);
    };

    return (
        <>
            {!sobreAbierto ? (
                <div className="sobre" ref={sobreRef}>
                    <div className="first" id="sobre_hoja1"></div>
                    <button className="sello" onClick={() => setSobreAbierto(true)}>
                        Abrir Invitación
                    </button>
                    <div className="second" id="sobre_hoja2"></div>
                </div>
            ) : (
                <div className="contenido">
                    <div className="top">
                        <img className="top-img" src={imagenes.flores2} alt="Decoración superior" />
                    </div>
                    <div className="bottom">
                        <img className="bottom-img" src={imagenes.flores} alt="Decoración inferior" />
                    </div>
                </div>
            )}
            <audio ref={musicRef} id="music" src="./assets/music.mp3"></audio>
            <div ref={sobreParamsRef} translateY="100%" duration="3"></div>
        </>
    );
};

export default Invitacion3;
