import React, { useState } from "react";
import imagenes from "./assets/imagenes"; 

const Galeria = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [modalImage, setModalImage] = useState("");

  const album = [
    ["img11", "img12"],
    ["img21", "img22"],
    ["img31", "img32"],
    ["img51", "img52"],
    ["img53", "img54"]
  ];

  const abrirModal = (imagen) => {
    setModalImage(imagen);
    setModalOpen(true);
  };

  const cerrarModal = () => {
    setModalOpen(false);
  };

  return (
    <>
      {/* Galería de imágenes */}
      <div className="galeria">
        <div className="container-grid">
          {album.map((fila, index) => (
            <div key={index} className="row"> 
              {fila.map((nombreImg) => (
                <img
                  key={nombreImg}
                  className="grid-item"
                  src={imagenes[nombreImg]}
                  alt={`Foto ${nombreImg}`}
                  onClick={() => abrirModal(imagenes[nombreImg])} 
                />
              ))}
            </div>
          ))}
        </div>
      </div>

      {/* Modal para imágenes ampliadas */}
      {modalOpen && (
        <div className="modal-overlay" onClick={cerrarModal}> 
          <div className="modal">
            <span className="close-btn" onClick={cerrarModal}>&times;</span>
            <img className="modal-img" src={modalImage} alt="Imagen ampliada" />
          </div>
        </div>
      )}
    </>
  );
};

export default Galeria;
